import React from "react";
import { Map, GoogleApiWrapper, Marker } from "google-maps-react";
import fetch from "node-fetch";

const mapStyles = {
  width: "100%",
  height: "100%"
};

class MapView extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: props.busroute, buses: []};
  }

  fetchBuses(){
    fetch(
        "http://developer.itsmarta.com/BRDRestService/RestBusRealTimeService/GetBusByRoute/" + this.state.busroute
      )
        .then(res => res.json())
        .then(json => this.formatData(json));
  }

formatData(data) {
      formatedData = data.map(datum => {
        return {
          route: datum["ROUTE"],
          lat: datum["LATITUDE"],
          lon: datum["LONGITUDE"],
          timepoint: datum["TIMEPOINT"]
        };
      });
      this.setState({buses: formatedData});
    }

  componentDidUpdate() {
    if (this.state.busroute && this.state.buses[0] && this.state.busroute !== this.state.buses[0].route) {
      this.fetchBuses();
    }
  }

  busPositions(lat, lon) {
      return <Marker position={{lat: lat, lng: lon}}/>;
  }

  render() {
    return (
      <Map
        google={this.props.google}
        zoom={8}
        style={mapStyles}
        initialCenter={{ lat: 33.749, lng: -84.388 }}>
            {this.state.buses ? this.state.buses.forEach(bus => this.busPositions(bus.lat, bus.lon)) : null}
        </Map>
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyB_Z9lTIAzbW2VBkebVJWWjGgTvJZLQk8o"
})(MapView);
