import React from "react";
import RouteInput from "./subviews/RouteInput";
import MapView from "./subviews/MapView";

class MainView extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: "", showMap: false};
  }

  submitRoute(route) {
    const showMap = route.length ? true : false;
    this.setState({ busroute: route, showMap: showMap});
  }

  render() {
    return <div><RouteInput submitRoute={this.submitRoute} /><MapView /></div>;
  }
}

export default MainView;
