import React from "react";
import RouteInput from "./subviews/RouteInput";
import MapView from "./subviews/MapView";

class MainView extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: "", buses: [] };

    this.submitRoute = this.submitRoute.bind(this);
  }

  fetchBuses() {
    fetch(
      "http://developer.itsmarta.com/BRDRestService/RestBusRealTimeService/GetBusByRoute/" +
        this.props.route
    )
      .then(res => res.json())
      .then(json => this.formatData(json));
  }

  formatData(data) {
    const formatedData = data.map(datum => {
      return {
        route: datum["ROUTE"],
        lat: datum["LATITUDE"],
        lon: datum["LONGITUDE"],
        timepoint: datum["TIMEPOINT"]
      };
    });
    this.setState({ buses: formatedData });
  }

  componentDidUpdate() {
    if (!this.state.buses.length) {
      this.fetchBuses();
    }
  }

  submitRoute(route) {
    this.setState({ busroute: route });
  }

  render() {
    return (
      <div>
        <div>
          <RouteInput submitRoute={this.submitRoute} />
        </div>
        <div>
          <MapView buses={this.state.busroute} />
        </div>
      </div>
    );
  }
}

export default MainView;
