import React from "react";
import { Map, GoogleApiWrapper, Marker } from "google-maps-react";
import fetch from "node-fetch";

const mapStyles = {
  width: "100%",
  height: "100%"
};

class MapView extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: props.busroute };
  }

  fetchBuses(){
    fetch(
        "http://developer.itsmarta.com/BRDRestService/RestBusRealTimeService/GetAllBus"
      )
        .then(res => res.json())
        .then(json => this.formatData(json));
  }

formatData(data) {
      formatedData = data.map(datum => {
        return {
          route: datum["ROUTE"],
          lat: datum["LATITUDE"],
          lon: datum["LONGITUDE"],
          timepoint: datum["TIMEPOINT"]
        };
      });
    }

  componentDidUpdate() {
    if (this.state.busroute) {
      this.fetchBuses();
    }
  }

  render() {
    return (
      <Map
        google={this.props.google}
        zoom={8}
        style={mapStyles}
        initialCenter={{ lat: 33.749, lng: -84.388 }}
      />
    );
  }
}

export default GoogleApiWrapper({
  apiKey: "AIzaSyB_Z9lTIAzbW2VBkebVJWWjGgTvJZLQk8o"
})(MapView);
