import React from "react";
import Grid from "@material-ui/core/Grid";
import fetch from "node-fetch";
import _ from "lodash";
import Program from "./Program";

class ProgramGrid extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      programs: [],
      moreInfo: false
    };
  }

  getPrograms() {
    if (!this.state.programs.length) {
      let programs = [];
      fetch("https://api.myjson.com/bins/5bdb3")
        .then(res => res.json())
        .then(json => {
          programs = json;
          fetch("https://api.myjson.com/bins/47axv")
            .then(res => res.json())
            .then(json => {
              for (let i = 0; i < programs.length; i++) {
                const programPriceData = json.filter(
                  priceData => priceData.ProgramID === programs[i].ProgramID
                );
                programs[i]["priceData"] = programPriceData;
              }
              this.setState({})
            });
        });
    }
  }

  render() {
    if (!this.state.programs.length) {
      this.getPrograms(true);
    }

    return (
      <div>
        <Grid container spacing={3}>
          <Program dataTime={"monkeystuff"} />
        </Grid>
      </div>
    );
  }
}

export default ProgramGrid;
