import React from "react";
import _ from "lodash";

class RouteSelect extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: "" };

    this.handleChange = this.handleChange.bind(this);
    this.handleEnterKey = this.handleEnterKey.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ busroute: event.target.value });
  }

  handleEnterKey(event) {
    if (event.key === "Enter") {
      this.handleSubmit(event);
    }
  }

  handleSubmit(event) {
    event.preventDefault();
    this.props.submitRoute(this.state.busroute);
  }

  render() {
    const busRoutes = _.sortby(_.uniqBy(this.props.buses, 'route'), (bus) => {
      return parseInt(bus.route, 10);
