import React from "react";
import _ from "lodash";

const scrollStyles = {
  width: "20%",
  height: "400px",
  overflowY: "scroll"
};

class RouteSelect extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: "", buses: props.buses };

    this.onClick = this.onClick.bind(this);
  }

  onClick(event) {
    debugger;
    event.preventDefault();
    this.props.submitRoute(event.target.innerText);
  }

  render() {
    const busRoutes = _.sortBy(_.uniqBy(this.props.buses, "route"), bus => {
      return parseInt(bus.route, 10);
    });
    return (
      <div style={scrollStyles}>
        <h3>Select Route</h3>
        <li value={"All"}>All</li>
        {busRoutes.map(busRoute => (
          <li key={busRoute.route.toString()} onClick={this.onClick}>
            {String(busRoute.route)}
          </li>
        ))}
      </div>
    );
  }
}

export default RouteSelect;
