import React from "react";
import RouteSelect from "./subviews/RouteSelect";
import MapView from "./subviews/MapView";
import fetch from "node-fetch";
import FlexView from "react-flexview";

const styles = {
  width: "400px",
  height: "400px"
};

class MainView extends React.Component {
  constructor(props) {
    super(props);
    this.state = { busroute: "", buses: [] };

    this.submitRoute = this.submitRoute.bind(this);
  }

  fetchBuses() {
    if (!this.state.buses.length) {
      fetch(
        "http://developer.itsmarta.com/BRDRestService/RestBusRealTimeService/GetAllBus"
      )
        .then(res => res.json())
        .then(json => this.formatData(json));
    }
  }

  formatData(data) {
    const formatedData = data.map(datum => {
      return {
        route: datum["ROUTE"],
        lat: datum["LATITUDE"],
        lon: datum["LONGITUDE"],
        timepoint: datum["TIMEPOINT"]
      };
    });
    this.setState({ buses: formatedData });
  }

  submitRoute(route) {
    this.setState({ busroute: route });
  }

  render() {
    this.fetchBuses();
    return (
      <div style={styles}>
        <FlexView hAlignContent="left">
          <RouteSelect
            submitRoute={this.submitRoute}
            buses={this.state.buses}
          />
          <div>
            <MapView buses={this.state.buses} />
          </div>
        </FlexView>
      </div>
    );
  }
}

export default MainView;
