import React from "react";

class RouteInput extends React.Component {
  constructor(props) {
    super(props);
  }

  handleInput = e => {
    if (e.key === "Enter") {
      debugger;
    }
  };

  render() {
    return <input type={"text"} onKeyDown={this.handleInput} />;
  }
}

const mapStateToProps = state => {
  return {
    books: state.books
  };
};

// Maps actions to props
const mapDispatchToProps = dispatch => {
  return {
    createBook: book => dispatch()
  };
};

export default (RouteInput);
